import React from 'react';
import styles from './index.module.scss';



export class GoodsShow extends React.Component {
    click = false;
    handleClick(click) {
        return !click;
    }

    render() {
        console.log(this.click);
        return (
            <div>
                <div className={this.click ? styles.bigImg : styles.img} onClick={this.handleClick(this.click)}></div>
                <div className={styles.content}></div>
            </div>
        );
    }
}
