import React, { useRef } from 'react';
import styles from './index.module.scss';


export class Login extends React.Component {


  username = useRef<HTMLInputElement>().current?.value;
  password = useRef<HTMLInputElement>().current?.value;

  right = true

  handleClick = () => {
    if((this.username === 'a') && (this.password === 'b')) {
      return this.right;
    }else {
      return !this.right;
    }
  }

  render() {
    return (
      <div className={styles.login}>
        <div className={styles.left}></div>
        <div className={styles.right}>
          <div className={styles.content}>
            <div>
              <div>账号：<input id="userName" type="text" ref={this.username} /></div>
              <div>密码：<input id="passWord" type="text" ref={this.password} /></div>
            </div>
          </div>
          <div className={styles.submit}>
            <button className={this.right ? styles.right : styles.error} onClick={this.handleClick()}>确定</button>
            <button>取消</button>
          </div>
        </div>
      </div>
    );
  }
}
