import React from 'react';
import styles from './index.module.scss';


class Square extends React.Component<any, any> {
    render() {
        return (
            <button className={styles.square} onClick={() => this.props.children}>{this.props.value}</button>
        );
    }
}

class Board extends React.Component<any, any> {
    constructor(props) {
        super(props);
        this.state = {
            squares: Array(9).fill(null),
        };
    }

    handleClick(i) {
        const squares = this.state.squares.slice();
        squares[i] = 'X';
        this.setState({ squares: squares })
    }

    renderSquare(i) {
        return (
            <Square value={this.state.squares[i]} onClick={() => this.handleClick(i)} />
        );
    }

    render() {
        const status = 'next player: X'
        return (
            <div>
                <div className={styles.status}>{status}</div>
                <div className={styles['board-row']}>
                    {this.renderSquare(0)}
                    {this.renderSquare(1)}
                    {this.renderSquare(2)}
                </div>
                <div className={styles['board-row']}>
                    {this.renderSquare(3)}
                    {this.renderSquare(4)}
                    {this.renderSquare(5)}
                </div>
                <div className={styles['board-row']}>
                    {this.renderSquare(6)}
                    {this.renderSquare(7)}
                    {this.renderSquare(8)}
                </div>
            </div>
        );
    }
}

export class Login extends React.Component {
    render() {
        return (
            <div className={styles.login}>
                <div className={styles['login-board']}><Board /></div>
                <div className={styles['login-info']}></div>
            </div>
        );
    }
}
