import React from 'react';
import styles from './index.module.scss';
import classNames from 'classnames';
import { Jump } from '../components/Jump';


export class GoodsShow extends React.Component<any, any> {
    constructor(props) {
        super(props);
        this.state = {
            click: false,
        }
    }
    handleClick(click) {
        this.setState({click: !click})
        return click;
    }

    render() {
        return (
            <div className={styles.container}>
                <div className={styles.imgShow}>
                    <div className={classNames(styles.img, this.state.click ? styles.bigImg : styles.img)} onClick={() => this.handleClick(this.state.click)}></div>
                </div>
                <div className={styles.jump}><Jump history={this.props.history} /></div>
            </div>
        );
    }
}
